         * @param param1 Parameter 1.
         * @param param2 Parameter 2.
         * @return A new instance of fragment BerandaFragment.
         */
        // TODO: Rename and change types and number of parameters
        @JvmStatic
        fun newInstance(param1: String, param2: String) =
            BerandaFragment().apply {
                arguments = Bundle().apply {
                    putString(ARG_PARAM1, param1)
                    putString(ARG_PARAM2, param2)
                }
            }
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        postingRecyclerView.adapter = postingAdapter
        postingRecyclerView.layoutManager = LinearLayoutManager(activity)
        postingRecyclerView.setHasFixedSize(true)



    }

    override fun onItemClick(position: Int) {
        //Toast.makeText(activity, "Item $position", Toast.LENGTH_SHORT).show()

